import React from "react";
import {Form, Table, Button,Icon, DatePicker, Select, Input, Card, Col, Row } from "antd";
import {Link} from 'react-router-dom'
import axios from 'axios';
const baseURL = 'http://74.127.61.115:9900/graphql';
const { RangePicker } = DatePicker;
const { Option } = Select;
const columns = [
  {
    title: 'Valor del dia',
    dataIndex: 'value',
    key: 'value',
  },
  {
    title: 'Pais de origen',
    dataIndex: 'origin_country.name',
    key: 'origin_country.name',
  },
  {
    title: 'Pais destino',
    dataIndex: 'destination_country.name',
    key: 'destination_country.name',
  },
  {
    title: 'Estipulado para',
    dataIndex: 'createdAt',
    key: 'createdAt',
  },   
];

class Bancos extends React.Component{
  state = {
    dataSource:[],
  }
  componentDidMount(){
    axios.post(baseURL,{
      query:`
        { 
          exchanges{
            edges{
              id,
              value,
              origin_country{
                name,
                currency{
                  id,
                  name,
                  short
                }
              },
              destination_country{
                name,
                currency{
                  id,
                  name,
                  short
                }
              },
              createdAt
            },
            pageInfo{
              hasNextPage,
              endCursor
            }
          }
      `
    }).then((res) => {
      this.setState({
        dataSource:res.data.data.exchanges.edges
      })
      console.log(res.data.data)
    }).catch((err) =>{
      console.log(err.message)
    })
  }

  render(){
    return (
      <div style={{padding: '30px' }}>
        <h3>TÃº Seccion de bancos asociados</h3>
        <Row gutter={16}>
          <Col span={8}>
            <Card title="Tasa de cambio" extra={<a href="#">Actualizar <Icon type="reload" /></a>} style={{ height:200, width: 300 }}>
              <h2><center>8.000 VEF</center></h2>
              <p>Datos pueden cambiar segun avance el dia</p>
            </Card> 
          </Col>
          <Col span={8}>        
          <Card  title="Busqueda Inteligente" extra={<Link to='/bancos/registro'>Nuevo Banco <Icon type="plus-circle" /></Link>} style={{ height:200, width: 650 }}>
            <Form layout='inline' onSubmit={this.handlSubmit}>
                <Form.Item label="Nombre del Banco">
                  <Input name='nombre'/>
                </Form.Item>          
                <Form.Item label="Nombre del asociado">
                  <Input name='asociado'/>                  
                </Form.Item>
                <Form.Item
                  wrapperCol={{
                    xs: { span: 24, offset: 0 },
                    sm: { span: 16, offset: 8 },
                  }}
                >
                  <Button type="primary" htmlType="submit">
                    Buscar<Icon type="search" />
                  </Button>
                </Form.Item>
              </Form>
          </Card>
        </Col>  
        </Row>
        <Table columns={columns} dataSource={this.state.dataSource}/>
      </div>
    );
  };  
}

export default Bancos;
